INSERT INTO "dataset" VALUES(739,'Sociálny fond','a) poskytovanie návratných sociálnych výpomocí zo sociálneho fondu,                                                     b) poskytovanie nenávratných sociálnych výpomocí zo sociálneho fondu,                                                                c) zoznam zmlúv a dodatkov o poskytnutí návratnej sociálnej výpomoci zo sociálneho fondu','MV SR','MV SR',2,2,'Za obdobie 01.01.2011 – 31.12.2012:                                                      a)poskytnuté nenávratné sociálne výpomoci zo sociálneho fondu – 80,                                                      b) poskytnuté návratné sociálne výpomoci zo sociálneho fondu – 80,                                                                                               c) zoznam zmlúv a dodatkov o poskytnutí návratnej sociálnej výpomoci zo sociálneho fondu - 85','štvrťročne','Poskytnuté nenávratné sociálne výpomoci zo sociálneho fondu a návratné výpomoci zo sociálneho fondu budú zverejnené na intranetovej stránke MV SR v súlade s čl. 4 ods. 24 Kolektívnej zmluvy pre príslušníkov PZ na rok 2012, čl. 5 ods. 18 Kolektívnej zmluvy pre štátnych zamestnancov a čl. 5 ods. 18 Kolektívnej zmluvy pre zamestnancov.                                                                                                                             Zoznamy budú obsahovať hodnosť, titul, meno, priezvisko žiadateľa, služobné/pracovné zaradenie, druh výpomoci, účel výpomoci, dátum doručenia žiadosti o výpomoc a dátum poskytnutia výpomoci v súlade s osobitnými predpismi (zákon č. 428/2002 Z. z. o ochrane osobných údajov v znení neskorších predpisov, zákon č. 211/2000 Z. z. o slobodnom prístupe k informáciám v znení neskorších predpisov). Zmluvy a dodatky o poskytnutí návratnej sociálnej výpomoci zo sociálneho fondu sú zverejnené na http://www.crz.gov.sk/.',4,'','2012-11-20',3);
INSERT INTO "dataset" VALUES(740,'Evidencia dopravných nehôd','Zaznamenáva, sústreďuje a poskytuje informácie najmä o všetkých obligatórnych údajoch, o vzniknutých a Policajnému zboru známych dopravných nehodách v cestnej premávke podľa § 67 zákona č. 8/2009 Z.z. o cestnej premávke a o zmene a doplnení niektorých zákonov. Na účely analýzy a vyhodnocovania týchto informácií pre činnosť útvarov Policajného zboru v rozsahu vymedzenom v § 68 zákona č. 8/2009 Z.z. o cestnej premávke a o zmene a doplnení niektorých zákonov v znení neskorších predpisov poskytuje časť informácií aj iným subjektom. ','MV SR','MV SR',1,4,'počet objektov v dátovom zdroji závisí od počtu zaevidovaných dopravných nehôd v príslušnom roku','online','Evidencia dopravných nehôd sa vedie na účely získavania informácií o stave dopravnej nehodovosti na území Slovenskej republiky a poskytovania údajov z tejto evidencie. Položková skladba databázy podľa § 67 zákona č. 8/2009 Z.z.',3,' osobné údaje',NULL,3);
