INSERT INTO "dataset" VALUES(1310,'RZSJ – Register základných sídelných jednotiek','Na zabezpečenie lokalizácie informácií pri ich zbere a sledovaní územno-technických a sociálno-ekonomických  javov, ktoré sú viazané k osídleniu územia slúži Register základných sídelných jednotiek. Pre RZSJ tvori východiskový legislatívny rámec doteraz platná vyhláška bývalého Federálneho štatistického úradu č. 120/1979 Zb. o priestorovej identifikácii informácií, ktorou je určená sústava priestorových jednotiek (PJ). ','MŽP SR','SAŽP',1,4,'7600','priebežne','',1,'','2013-04-30',1);
