INSERT INTO "dataset" VALUES(1326,'Informačný systém o kvalite vody na kúpanie','IS o vodách na kúpanie zabezpečujúci zber, hodnotenie a spracovanie informácií o kvalite vôd na kúpanie pre plnenie reportingových povinností Slovenska a pre informovanie verejnosti, ktorý je prevádzkovaný SAŽP a napĺňaný údajmi z činnosti regionálnych úradov verejného zdravotníctva. ','MŽP SR','Úrad verejného zdravotníctva Slovenskej republiky, SAŽP',1,4,'160 záznamov',' priebežne','',2,'Pre verejosť sú zverejnené len vybrané agregované informácie a správy uložené v systéme','2013-03-31',1);
INSERT INTO "dataset" VALUES(1327,'Informačný systém o pitnej vode','IS o pitnej vode je IS na realizáciu pre zber, hodnotenie a spracovanie informácií o kvalite pitnej vody pre plnenie reportingu a harmonizovanie dátových tokov medzi rezortami životného prostredia a zdravotníctva. ','MŽP SR','Úrad verejného zdravotníctva Slovenskej republiky, SAŽP',1,4,'v aktualizácii',' priebežne','',2,'Pre verejosť sú zverejnené len vybrané agregované informácie a správy uložené v systéme','2013-01-31',1);
INSERT INTO "dataset" VALUES(1401,'Výkaz o výsledkoch následných finančných kontrol za rok 2010','Výkaz je súčasťou Ročnej správy o výsledkoch následných finančných kontrol, ktorá je predkladaná MF SR v zmysle vyhlášky MF SR č. 21/2009 Z. z, ktorou sa vykonávajú niektoré ustanovenia zákona č. 502/2001 Z. z. finančnej kontrole a vnútornom audite a o zmene a doplnení niektorých zákonov v znení neskorších predpisov','MPRV SR','MPRV SR',1,1,'','2/25/2011','-druh verejných prostriedkov,-počet vykonaných kontrol,-suma kontrolovaných verejných prostriedkov,-počet kontrolných zistení,-počet porušení finančnej disciplíny (podľa druhu).',1,'','2013-01-31',1);
