INSERT INTO "dataset" VALUES(1469,'Databáza o cudzorodých látkach','Databáza o cudzorodých látkach je prostriedkom pre zber, spracovanie, uchovávanie a poskytovanie informácií o sledovaných látkach v krmivách, potravinách, vode a pôde','MPRV SR','Výskumný ústav potravinársky (VÚP)',1,1,'10 miliónov údajov',' vzniku 1986, mesačne','Evidujú sa údaje v kategóriách: Komodita, Počet analýz, Počet vzoriek, Počet nadlimitných vzoriek (NL), % nadlimitných vzoriek (NL), Cudzorodé látky, limit, priemer, maximum, percento limitu (%), okres, štát, kraj, parametre, rok.',3,'Údaje v databáze predstavujú zdrojové údaje, ktoré neodborník nedokáže správne vyhodnotiť a dochádzalo by k dezinterpretácii údajov o riziku konzumácie potravín a šíreniu poplašných správ o kontaminácii potravín cudzorodými látkami. ',NULL,3);
